mapper.select:
SELECT fee_id AS feeId, user_id AS userId, park_id AS parkId, pay_park_month AS payParkMonth, pay_park_money AS payParkMoney, pay_park_status AS payParkStatus, pay_time AS payTime, create_time AS createTime, update_time AS updateTime FROM fee_park
AND user_id = #{userId}
AND pay_park_status = #{payStatus}
ORDER BY pay_park_month DESC LIMIT #{offset}, #{limit}

SELECT COUNT(*) FROM fee_park


        AND user_id = #{userId}


        AND pay_park_status = #{payStatus}
